def text(choice:int,value:float):
    if (choice == 1):
        value = (value * 9/5) + 32
        create_sprite_fahr()
        game.show_long_text("Temp converted to Fahrenheit is " + Math.round_with_precision(value, 2), DialogLayout.BOTTOM)
    else:
        value = (value - 32) * 5/9
        create_sprite_cels()
        game.show_long_text("Temp converted to Celsius is " + Math.round_with_precision(value, 2), DialogLayout.BOTTOM)

choice = select()
temp = value()

text(choice,temp)